mapper.select:
SELECT od_date, get_code_value('OR', od_status) as od_status, prd_name, od_detail_qty, od_detail_price, prd_no, ord.od_no as od_no, od_detail_no FROM orders ord JOIN ( SELECT pr.prd_name, od.od_no, od.prd_no, od.od_detail_qty, od.od_detail_price, od.od_detail_no FROM order_detail od JOIN product pr ON od.prd_no = pr.prd_no ) prd ON ord.od_no = prd.od_no WHERE us_no = ${us_no}
AND REGEXP_REPLACE(LOWER(REPLACE(prd_name,' ','')), '[[:punct:]]', '') LIKE '%'||REGEXP_REPLACE(LOWER(REPLACE(#{keyword},' ','')), '[[:punct:]]', '')||'%'
AND to_char(od_date, 'YYYY.MM.DD') BETWEEN #{startDate} and #{endDate}
ORDER BY od_date DESC
