mapper.select:
SELECT pg_no, pg_name, prd_no, prd_org_price, prd_price, prd_category, prd_date FROM ( SELECT ROWNUM AS rnum, pg_no, pg_name, prd_no, prd_org_price, prd_price, prd_category, prd_date FROM ( SELECT pg.pg_no, pg.pg_name, p.prd_no, p.prd_org_price, p.prd_price, p.prd_category, p.prd_date, ROW_NUMBER() OVER(PARTITION BY pg.pg_no ORDER BY pg.pg_no ) AS row_num FROM product p LEFT JOIN product_group pg ON p.pg_no = pg.pg_no
ORDER BY prd_price, pg_no
ORDER BY prd_price desc, pg_no
ORDER BY prd_date desc, pg_no
ORDER BY pg_no
ORDER BY pg_no
) a WHERE a.row_num = 1
AND prd_category = #{prd_category, jdbcType=VARCHAR}
AND ROWNUM <= ${pager.endRowNo} ) WHERE rnum >= ${pager.startRowNo}

SELECT pg_name
from product_group
where pg_no = ${pg_no}
