mapper.select:

select pg_no, pg_name
from (
   select rownum as rnum, pg_no, pg_name 
   from (
      select pg_no, pg_name
      from product_group
          where pg_no in (
             select distinct pg_no
             from product)
      order by pg_no
   )
   where rownum <= ${endRowNo}
)
where rnum >= ${startRowNo}

SELECT pg_name
from product_group
where pg_no = ${pg_no}
