mapper.select:
SELECT count(*) FROM ( SELECT ROWNUM AS rnum, pg_no, pg_name, prd_no, prd_org_price, prd_price, prd_category, prd_date, brand, status, message FROM ( SELECT pg.pg_no, pg.pg_name, p.prd_no, p.prd_org_price, p.prd_price, p.prd_category, p.prd_date, ROW_NUMBER() OVER(PARTITION BY pg.pg_no ORDER BY pg.pg_no ) AS row_num, (SELECT fil_brand FROM filter f WHERE f.prd_no = p.prd_no) AS brand, (SELECT fil_status FROM filter f WHERE f.prd_no = p.prd_no) AS status, (SELECT fil_msg FROM filter f WHERE f.prd_no = p.prd_no) AS message FROM product p LEFT JOIN product_group pg ON p.pg_no = pg.pg_no
AND REGEXP_REPLACE(LOWER(REPLACE(pg_name,' ','')), '[[:punct:]]', '') LIKE '%'||REGEXP_REPLACE(LOWER(REPLACE(#{keyword},' ','')), '[[:punct:]]', '')||'%'
) a WHERE a.row_num = 1
AND prd_category = #{prd_category, jdbcType=VARCHAR}
AND brand IN ( #{brand, jdbcType=VARCHAR} )
AND status IN ( #{status, jdbcType=VARCHAR} )
AND message IN ( #{message, jdbcType=VARCHAR} )
)

SELECT pg_name
from product_group
where pg_no = ${pg_no}
