mapper.select:
	  SELECT *
	  FROM (
	    SELECT t.*, ROWNUM AS RNUM
	    FROM (
            select review.*
			from review, product
			where review.prd_no = product.prd_no
			and product.pg_no = #{pg_no} 

			   AND REGEXP_REPLACE(LOWER(REPLACE(rev_content,' ','')), '[[:punct:]]', '') LIKE '%'||REGEXP_REPLACE(LOWER(REPLACE(#{keyword},' ','')), '[[:punct:]]', '')||'%'

	    ) t
	    WHERE  ROWNUM <= #{endRow} 
	  )
	  WHERE  RNUM >= #{startRow}


        SELECT *
		  FROM (
		    SELECT t.*, ROWNUM AS RNUM
		    FROM (
	           select review.*
				from review, product
				where review.prd_no = product.prd_no
				and product.pg_no = #{pg_no}
		        order by review.rev_star_rate DESC
		    ) t
		    WHERE  ROWNUM <= #{endRow} 
		  )
		  WHERE RNUM >= #{startRow}


        SELECT *
		  FROM (
		    SELECT t.*, ROWNUM AS RNUM
		    FROM (
	            select review.*
				from review, product
				where review.prd_no = product.prd_no
				and product.pg_no = #{pg_no}
			    order by review.rev_date DESC
		    ) t
		    WHERE  ROWNUM <= #{endRow} 
		  )
		  WHERE RNUM >= #{startRow}


        SELECT *
		  FROM (
		    SELECT t.*, ROWNUM AS RNUM
		    FROM (
	            select *
				from (select review.*
						from review, product
						where review.prd_no = product.prd_no
						and product.pg_no = #{pg_no})
				where rev_star_rate = #{starAmount}
		    ) t
		    WHERE  ROWNUM <= #{endRow} 
		  )
		  WHERE RNUM >= #{startRow}

		SELECT count(*)
		FROM review, product
		WHERE review.prd_no = product.prd_no
        and product.pg_no = ${pg_no}
        and review.rev_star_rate = ${rate}


	  SELECT *
	  FROM (
	    SELECT t.*, ROWNUM AS RNUM
	    FROM (
            select review.*
            from review, product
            where review.prd_no = product.prd_no
            and product.pg_no = #{pg_no}
	    ) t
	    WHERE ROWNUM <= #{endRow}
	  )
	  WHERE RNUM >= #{startRow}
